mapper.select:
SELECT * FROM ( SELECT (SELECT cascade_id_ FROM aos_org WHERE id_ = (SELECT org_id_ FROM aos_user WHERE id_ = aos_role.create_by_)) AS org_cascade_id_,
FROM aos_role
AND name_ LIKE '%${name_}%'
AND type_ = #{type_}
) t WHERE t.org_cascade_id_ LIKE '${org_cascade_id_}%' ORDER BY id_ DESC

SELECT aos_org.name_ as org_name_, aos_user_role.id_ as user_role_id_,
FROM aos_user LEFT JOIN aos_user_role ON aos_user_role.user_id_ = aos_user.id_ LEFT JOIN aos_org ON aos_org.id_ = aos_user.org_id_ WHERE aos_user.id_ IN (SELECT user_id_ FROM aos_user_role WHERE role_id_ = #{role_id_}) AND aos_user_role.role_id_ = #{role_id_}
AND (aos_user.name_ LIKE '%${na_}%' OR aos_user.account_ LIKE '%${na_}%')
AND aos_user.is_del_ = #{is_del_}
ORDER BY aos_user.id_ DESC
