mapper.select:
SELECT fm.id_, fp.name_ platform_id_, ftc.name_ third_channel_, fm.order_id_, fm.target_account_, fm.trans_amount_, ffb.contract_id_, fm.request_time_ AS request_time, fm.reponse_time_ AS response_time, fm.flow_biz_id_, fm.third_channel_fee_, fm.arrived_time_ , fm.record_time_, fm.request_time_, fm.reponse_time_, ffb.id_ flow_biz_id_ FROM fc_flow_money fm, fc_flow_business ffb, fc_platform fp, fc_third_channel ftc WHERE fm.flow_biz_id_ = ffb.id_ AND fm.platform_id_ = fp.id_ and ftc.id_ = fm.third_channel_ and fm.trade_type_ = 2
and fm.platform_id_ = #{platform_id_}
and ftc.id_ = #{third_channel_}
and substr(fm.arrived_time_,1,10) >= #{arrived_start_time_}
and substr(fm.arrived_time_,1,10) <= #{arrived_end_time_}
and substr(fm.record_time_,1,10) >= #{record_start_time_}
and substr(fm.record_time_,1,10) <= #{record_end_time_}
and fm.order_id_ = #{order_id_}
and fm.record_time_ > fm.arrived_time_
and fm.record_time_ <= fm.arrived_time_

SELECT fm.id_, fp.name_ platform_id_, ftc.name_ third_channel_, fm.order_id_, fm.target_account_, fm.trans_amount_, ffb.contract_id_, fm.request_time_ AS request_time, fm.reponse_time_ AS response_time, fm.flow_biz_id_, fm.third_channel_fee_, fm.arrived_time_ , fm.record_time_, fm.request_time_, fm.reponse_time_, ffb.id_ flow_biz_id_ FROM fc_flow_money fm, fc_flow_business ffb, fc_platform fp, fc_third_channel ftc WHERE fm.flow_biz_id_ = ffb.id_ AND fm.platform_id_ = fp.id_ and ftc.id_ = fm.third_channel_ and fm.trade_type_ = 2
and fm.platform_id_ = #{platform_id_}
and ftc.id_ = #{third_channel_}
and substr(fm.arrived_time_,1,10) >= #{arrived_start_time_}
and substr(fm.arrived_time_,1,10) <= #{arrived_end_time_}
and substr(fm.record_time_,1,10) >= #{record_start_time_}
and substr(fm.record_time_,1,10) <= #{record_end_time_}
and fm.order_id_ = #{order_id_}
and fm.record_time_ > fm.arrived_time_
and fm.record_time_ <= fm.arrived_time_

SELECT fo.product_code_, fo.product_name_, fo.loan_pattern_, fo.loan_staging_amount_, fo.loan_staging_duration_, fo.annualized_rate_, fo.contract_pay_date_, fo.contract_amount_, fo.expected_interest_, fo.expected_fee_, fo.status_, fo.customer_name_ name_, fo.customer_idcard_ idcard_, fo.customer_cellphone_ cellphone_, fo.contract_code_, fo.channel_, fo.customer_code_ customer_id_ FROM fc_contract fo WHERE fo.id_ = #{contract_id_}
and fo.contract_code_ = #{contract_code_}
and fo.customer_name_ = #{name_}
and fo.customer_idcard_ = #{idcard_}
and fo.product_name_ = #{product_name_}
and fo.channel_ = #{channel_}
and fo.status_ = #{status_}
